* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
// javascipt plugin for creating charts
import Chart from "chart.js";
import { withCookies } from "react-cookie";
import Cookies from 'js-cookie';
import { Redirect } from "react-router-dom";
import LoginLayout from "views/examples/Login.js";

// reactstrap components
import {
  Container,
  Row,
  Col
} from "reactstrap";

// core components
import {
  chartOptions,
  parseOptions,
} from "variables/charts.js";

import Header from "components/Headers/Header.js";

class Index extends React.Component {
    state = {
      wireless: [],
      cookie:  this.props.cookies.get('token')
    }
  componentDidMount(){
    if(this.props.cookies.get('token')){
      fetch('http://127.0.0.1:8000/api/wireless/', {
        method: 'GET',
        headers: {
          'Authorization': `Token ${this.props.cookies.get('token')}`
        }
      }).then( resp => resp.json()).then( res => this.setState({wireless: res}, function() {

      }))
      .catch(error => console.log(error))
    }else{

    }
  }

  constructor(props){
    let token =  this.props.cookies.get('token')
    alert(this.props.token)
    super(props);
    this.state = {
      activeNav: 1,
      chartExample1Data: "data1"
    };
    if (window.Chart) {
      parseOptions(Chart, chartOptions());
    }
  }
  toggleNavs = (e, index) => {
    e.preventDefault();
    this.setState({
      activeNav: index,
      chartExample1Data:
        this.state.chartExample1Data === "data1" ? "data2" : "data1"
    });
  };
  render() {
  alert(this.props.cookies.get('token'))
  if(this.props.cookies.get('token')){
    return (
          <>
            <Header />
            {/* Page content */}
            <Container className="mt--7" fluid>
              <Row className="mt-5">
                <Col className="mb-5 mb-xl-0" xl="8">
                </Col>
              </Row>
            </Container>
          </>,  alert('Usuário não autenticado222')
        );
    }else{
     return (
            alert('Usuário não autenticado'),
            window.location.href = ('/auth/login')
        )
    };
  }
}

export default withCookies(Index);
