/*!

=========================================================
* Argon Dashboard React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import axios from "axios";

// reactstrap components
import { Card, CardBody, CardTitle, Container, Row, Col, Button } from "reactstrap";

const api = {
    baseUrl: `http://127.0.0.1:8000/api/wifi/`,
    urlToken: `http://localhost:8000/api/auth/`
};


class Header extends React.Component {
      /* Primeira letra de uma string maiscula */
      capitalize = (s) => {
        if (typeof s !== 'string') return ''
        return s.charAt(0).toUpperCase() + s.slice(1)
      }

      constructor(props) {
        super(props);
        this.state = {
            wifi: []
        };
    }
        componentDidMount(){
        axios.get(
            api.baseUrl, { headers: {"Authorization" : `Token 90983948ea3a66581227202c25183ef2e34ac430`} }
        ).then((res) => {
            this.setState({wifi: res.data.wireless})
            //console.log(res.data.wireless)
        })
    }

    teste = event => {
        alert('click');
    }

  render(props) {
    return (
    <React.Fragment>
      <>
        <div className="header bg-gradient-info pb-8 pt-5 pt-md-8">
          <Container fluid>
            <div className="header-body">
              {/* Card stats */}
              <Row>{this.state.wifi.map(w =>
                <Col lg="6" xl="3">
                   <Card className="card-stats mb-xl-5">
                      <CardBody>

                        <CardBody key={w.id}><img src="https://image.flaticon.com/icons/svg/2916/2916682.svg" width="30px"></img>{w}</CardBody>
                        <Button className="primary" type="text" onClick={this.teste}>Enviar</Button>
                        </CardBody>
                   </Card>
                </Col>)}
              </Row>
            </div>
          </Container>
        </div>
      </>
      </React.Fragment>
    );
  }
}

export default Header;
