import React from "react";
import axios from "axios";

// reactstrap components
import { Card, CardBody, CardTitle, Container, Row, Col } from "reactstrap";

const api = {
    baseUrl: `http://127.0.0.1:8010/api/wifi/`,
    urlToken: `http://localhost:8010/api/token/`
};


class Header extends React.Component {
      /* Primeira letra de uma string maiscula */
      capitalize = (s) => {
        if (typeof s !== 'string') return ''
        return s.charAt(0).toUpperCase() + s.slice(1)
      }

      constructor(props) {
        super(props);
        this.state = {
            wifi: []
        };
    }
        componentDidMount(){
        axios.get(
            api.baseUrl, { headers: {"Authorization" : "Token 90983948ea3a66581227202c25183ef2e34ac430"} }
        ).then((res) => {
            //console.log(res.data.wifi)
            this.setState({wifi: res.data.wifi})
            //this.wifi = res.data.wifi
            console.log(this.state.wifi)
            //console.log(res.data.wifi)
        })
        //console.log(this.wifi.ssid)
    }
/*
      componentDidMount(){
        //fetch data
        fetch('http://127.0.0.1:8000/api/wifi/', {
          method: 'GET',
          headers: {
            'Authorization': 'Token 90983948ea3a66581227202c25183ef2e34ac430'
          }
        }).then( resp => resp.json()).then( res => this.setState({wifi: res}, function() {

        }))
        .catch(error => console.log(error))
      }*/
  render(props) {
    return (
      <>
                   {this.state.wifi.map(w =>
                               <ul>
                                <React.Fragment>
                                    <br />
                                    <h2>{w.ssid}</h2>
                                    <br />
                                    {w.signal}
                                    <br />
                                    {w.teste}
                                    <br />
                                </React.Fragment>
                            </ul>

                    )}


      </>
    );
  }
}

export default Header;
