/*!

=========================================================
* Argon Dashboard React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
// javascipt plugin for creating charts
import Chart from "chart.js";
import { withCookies } from "react-cookie";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';
import ListNotes from './ListNotes';
// reactstrap components
import {
  Container,
  Row,
  Col,
  Button
} from "reactstrap";

// core components
import {
  chartOptions,
  parseOptions,
} from "variables/charts.js";

import Header from "components/Headers/Header.js";

class Index extends React.Component {

  componentDidMount(){
       {this.sucess()}
        fetch('http://127.0.0.1:8000/api/v1/notes/', {
        method: 'GET',
        headers: {
          'Authorization': `Token ${this.props.cookies.get('token')}`
        }
      }).then( resp => resp.json()).then( res => this.setState({notes: res}, function() {
            alert(this.state.notes)
            console.log(this.state.notes)
            console.log(this.props.cookies.get('token'))
      }))
      .catch(error => console.log(error))
    }

   sucess = () => toast.success("Usuário logado com sucesso!");
   warning = () => toast.warning("Usuário não autenticado!");

  constructor(props){
    super(props);

    this.state = {
      activeNav: 1,
      chartExample1Data: "data1",
      notes: [],
      current_note_id: 0,
      is_creating: true
    };

    this.handleItemClick = this.handleItemClick.bind(this);
    this.handleAddItem = this.handleAddItem.bind(this);

    if (window.Chart) {
      parseOptions(Chart, chartOptions());
    }
  }
  toggleNavs = (e, index) => {
    e.preventDefault();
    this.setState({
      activeNav: index,
      chartExample1Data:
        this.state.chartExample1Data === "data1" ? "data2" : "data1"
    });
  };

    handleItemClick(id){
        this.setState((prevState) => {
            return{is_creating: false, current_note_id: id}
        })
    }

    handleAddItem(){
        this.setState((prevState) => {
            return{is_creating: true}
        })
    }

    render() {
        let token = this.props.cookies.get('token')
        if(token == null || token == "undefined"){
        return (
            window.location.replace('/auth/login')
        )
        }else{
          return (
          <>
            <Header />
            <Container className="mt--7" fluid>
              <Row className="mt-5">
                  <ToastContainer />
                <Col xs="2">
                    <Button color="primary" onClick={this.handleAddItem}>Criar um novo</Button>
                </Col>
                <Row>
                    <Col xs="4">
                        <ListNotes notes={this.state.notes} handleItemClick={(id) => this.handleItemClick(id)} />
                    </Col>
                    <Col xs="8">
                       <p>Content</p>
                       {
                        this.state.is_creating ? "Creating now" : `Editing with id: ${this.state.current_note_id}`
                       }
                    </Col>
                </Row>
              </Row>
            </Container>
          </>
        );
        }
    }
  }

export default withCookies(Index);
