/*!

=========================================================
* Argon Dashboard React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
// javascipt plugin for creating charts
import Chart from "chart.js";
import { withCookies } from "react-cookie";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.min.css';
// reactstrap components
import { Card, CardBody, Container, Row, Col, Button } from "reactstrap";

class Pentest extends React.Component {
    render() {
        const mac = new URLSearchParams(window.location.search).get('mac');
        const ssid = new URLSearchParams(window.location.search).get('ssid');
          return (
          <React.Fragment>
          <>
            <div className="header bg-gradient-info pb-8 pt-5 pt-md-8">
              <Container fluid>
                <div className="header-body">
                  {/* Card stats */}
                  <Row>
                    <Col lg="6" xl="3">
                        <div className="container">
                       <Card className="card-stats mb-xl-5">
                        <CardBody style={{'whiteSpace': 'pre-line'}}><img src="https://image.flaticon.com/icons/svg/2916/2916682.svg" alt="wireless" width="30px" align="left"></img>
                            <p>MAC: {mac}</p>
                            <p>SSID: {ssid}</p>
                        </CardBody>
                       </Card>
                       </div>
                    </Col>
                  </Row>
                </div>
              </Container>
            </div>
          </>
          </React.Fragment>
        );
       }
    }

export default withCookies(Pentest);
